mapper.select:
SELECT 
FROM sys_room r
LEFT JOIN sys_room_type t ON r.type_id = t.id


        AND r.room_number LIKE CONCAT('%', #{roomNumber}, '%')


        AND r.type_id = #{typeId}


        AND r.floor = #{floor}


        AND r.status = #{status}


ORDER BY r.room_number

SELECT COUNT(*)
FROM sys_room r


        AND r.room_number LIKE CONCAT('%', #{roomNumber}, '%')


        AND r.type_id = #{typeId}


        AND r.floor = #{floor}


        AND r.status = #{status}

SELECT 
FROM sys_room r
LEFT JOIN sys_room_type t ON r.type_id = t.id
WHERE r.status = 0

    AND r.type_id = #{typeId}


    AND r.price >= #{minPrice}


    AND r.price <= #{maxPrice}

ORDER BY r.price

SELECT COUNT(*)
FROM sys_room
WHERE room_number = #{roomNumber}

    AND id != #{excludeId}
